    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        //ENTRAR
        String query = "Select u From Usuario u where u.login = :login and u.senha = :senha";
        query2 = entityManager2.createQuery(query);
        query2.setParameter("login", jTextField1.getText());
        query2.setParameter("senha", jPasswordField1.getText());
        list2.clear();
        list2.addAll(query2.getResultList());
        
        if (list2.size() == 1) {
            JfrmPrincipal.main(null);
            dispose();
        } else {
            JOptionPane.showMessageDialog(null, "Usu√°rio ou senha incorretos", "ERRO", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

